package arthmetic_operators;

public class arthop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a=20;
	int b=40;
	System.out.println(a+b);
	System.out.println(a-b);
	System.out.println(a*b);
	System.out.println(a/b);
	System.out.println(a%b);
	System.out.println(++a);
	System.out.println(++b);
	System.out.println(--b);
	System.out.println(--a);
	

	}

}
