package assignmentop;

public class assignmentop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=3;
		System.out.println(x+=5);          //x+5=>3+5=8
		System.out.println(x-=5);         //x-5=>8-5=3
		System.out.println(x*=5);           //x*5=>3*5=15
		System.out.println(x/=5);          //x/5=>15/5=3     
		System.out.println(x%=5);           //x+%5=>3%5=3    this is remainder 
		
		

	}

}
